# 牛丼
gyudon = Recipe.create(title: '家で本格の味!吉野家の牛丼を再現', comment: '有名チェーン店の牛丼の味を再現してみました!', amount: 4, calorie: 733, main_ingredient: '牛肉', category: 'ごはんもの', tips: '玉ねぎはくし切りに切る。<br>味付けは甘めが美味しいですね。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyudon/%E3%83%A1%E3%82%A4%E3%83%B3.jpeg', user_id: 1)
gyudon.ingredients.create([{index: 0, name: '水', amount: '100cc'}, {index: 1, name: '白ワイン', amount: '100cc'}, {index: 2, name: '砂糖', amount: '大さじ1'}, {index: 3, name: 'みりん', amount: '50cc'}, {index: 4, name: '昆布5cm角', amount: '1枚'}, {index: 5, name: 'ほんだし', amount: '小さじ1'}, {index: 6, name: '醤油', amount: '50cc'}, {index: 7, name: '牛バラ肉(オーストラリア産)', amount: '300g'}, {index: 8, name: '玉ねぎ中玉', amount: '2個'}, {index: 9, name: '紅生姜', amount: '適量'}, {index: 10, name: '卵', amount: '適量'}, ])
gyudon.procedures.create([{index: 0, description: '玉ねぎはくし切りに切る。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyudon/%E7%8E%89%E3%81%AD%E3%81%8D%E3%82%99%E3%82%92%E5%88%87%E3%82%8B.jpeg'}, {index: 1, description: 'だし汁材料を鍋に入れ沸騰させる。', image_url: ''}, {index: 2, description: '沸騰した昆布をとり出し、牛肉を入れる。', image_url: ''}, {index: 3, description: '牛肉の色が変わる(火がとおる)寸前に鍋から一旦全部出す。', image_url: ''}, {index: 4, description: '牛肉を出した鍋に玉ねぎを入れ、沸騰したら蓋をして弱火で20分待つ。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyudon/%E7%85%AE%E3%82%8B.jpeg'}, {index: 5, description: '牛肉を鍋に戻し、火を通す。', image_url: ''}, {index: 6, description: 'ご飯の上にかければ、牛丼の完成！', image_url: ''}, ])

# ツナときのこのトマトパスタ
tunaAndMushroomPasta = Recipe.create(title: 'ツナときのこのトマトパスタ', comment: 'ツナとキノコのきこり風トマトパスタ。ボスカイオーラともいいます。シンプル。包丁なしフライパンのみでおいしく作れます。', amount: 2, calorie: 713, main_ingredient: '果菜', category: 'パスタ', tips: 'コツはとくにありません。誰でも簡単においしくつくれると思います。材料欄には書きませんでしたが、アンチョビ、オレガノ、イタリアンパセリがあるとよりおいしくなります。今回、きのこはまいたけを使いましたが、しめじ、エリンギなどもおすすめです。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/tunaAndMushroomPasta/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpeg', user_id: 1)
tunaAndMushroomPasta.ingredients.create([{index: 0, name: 'オリーブオイル', amount: '大さじ４'}, {index: 1, name: 'ニンニク（チューブ）', amount: '３〜４cm'}, {index: 2, name: 'きのこ（お好きなもの）', amount: 'ひとかぶ'}, {index: 3, name: 'ツナ缶（ノンオイル）', amount: '２缶'}, {index: 4, name: '白ワイン（なくても可）', amount: '８０cc'}, {index: 5, name: 'トマト缶', amount: '１缶'}, {index: 6, name: '塩', amount: '大さじ２'}, {index: 7, name: 'スパゲティ', amount: '一人８０gくらい'},  {index: 8, name: 'こしょう', amount: 'おこのみで'}, ])
tunaAndMushroomPasta.procedures.create([{index: 0, description: '最初にパスタ用のお湯を沸かし，フライパンにオリーブオイル、にんにくを入れ弱火で炒めます。今回は、アンチョビも入れました。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/tunaAndMushroomPasta/%E6%89%8B%E9%A0%861.jpeg'}, {index: 1, description: 'にんにくのいい香りがしてきたら、きのこを入れて中火で炒めます。今回はまいたけを使いました。ちょっと焦げ目がつくくらい。', image_url: ''}, {index: 2, description: 'ノンオイルのツナ缶を汁ごといれ、中火のまま少し炒めます。', image_url: ''}, {index: 3, description: '白ワインを入れて、中火で煮詰め、アルコールを飛ばします。', image_url: ''}, {index: 4, description: 'トマト缶を入れ、トマトをつぶして煮詰めます。火は少し弱めた方がいいかも。だいたい２０分くらい煮詰めるので→', image_url: ''}, {index: 5, description: '途中でスパゲティをゆで始めます。水１リットルあたり大さじ１の塩を入れます。', image_url: ''}, {index: 6, description: 'ソースを煮詰めるとこんな感じになります。オレガノを入れると２倍くらいおいしくなります。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/tunaAndMushroomPasta/%E6%89%8B%E9%A0%867.jpeg'}, {index: 7, description: 'フライパンにスパゲティを入れてあえます。お玉１～２杯のゆで汁を入れてください。', image_url: ''}, {index: 8, description: '塩こしょうで味を調え、盛り付けをして完成です。まいたけしゃきしゃきしておいしい。', image_url: ''}, ])

# 唐揚げ
karaage = Recipe.create(title: 'ザクザク食感が楽しい♡鶏の唐揚げ', comment: '下味をつけた鶏肉に衣をしっかりつけてザクザク食感が楽しめる鶏の唐揚げです♡', amount: 2, calorie: 415, main_ingredient: '鶏肉', category: '揚げ物', tips: '鶏を油に入れる際はしっかりボールにくっついている衣を箸などでくっつける(ザクザク食感)
よりザクザク感をupしたい場合は水と片栗粉の量を大さじ1ほど微調整して衣を多めにしてください♡お好みで♪', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/karaage/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpeg', user_id: 1)
karaage.ingredients.create([{index: 0, name: '鶏モモ肉', amount: '１枚'}, {index: 1, name: 'お酒', amount: '大さじ１'}, {index: 2, name: '醤油', amount: '大さじ１'}, {index: 3, name: '水', amount: '大さじ１'}, {index: 4, name: 'ニンニクすりおろし', amount: '小さじ２'}, {index: 5, name: 'ショウガ', amount: '小さじ２'}, {index: 6, name: '片栗粉', amount: '大さじ５'}, {index: 7, name: 'すりごま', amount: '大さじ１'}, {index: 8, name: '油', amount: '鍋に合わせて鶏肉が浸かるくらい'}, ])
karaage.procedures.create([{index: 0, description: '鶏モモ肉を８等分に切る
ボールに入れ、お酒、醤油、水、ニンニクすりおろし、ショウガすりおろしを入れる', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/karaage/%E6%89%8B%E9%A0%861.jpeg'}, {index: 1, description: 'しっかり混ぜ込み、ラップをして１時間冷蔵庫で寝かせる', image_url: ''}, {index: 2, description: '片栗粉、すり胡麻(お好みで)を入れて混ぜる', image_url: ''}, {index: 3, description: '水気が無くなり、鶏同士がくっつくくらいが目安', image_url: ''}, {index: 4, description: '180℃の油で約5～６分揚げる<br>鶏にボールにくっついている衣をしっかりつけながら油に入れていく<br>途中ひっくり返す', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/karaage/%E6%89%8B%E9%A0%865.jpeg'}, {index: 5, description: 'キレイな色に揚がれば出来上がり♪', image_url: ''}, ])

# ハンバーグ
hamburgSteak = Recipe.create(title: '簡単！煮込みハンバーグ', comment: 'トマトケチャップ＋とんかつソースで簡単にデミグラス風に！', amount: 4, calorie: 304, main_ingredient: 'ひき肉', category: '肉料理', tips: 'ハンバーグは煮込むことでしっかり火が通り、ふっくらと仕上がります。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/hamburg/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpg', user_id: 1)
hamburgSteak.ingredients.create([{index: 0, name: '○ 合挽肉', amount: '300g'}, {index: 1, name: '○ 玉ねぎ', amount: '中1/2個'}, {index: 2, name: '○ パン粉', amount: '1/3カップ'}, {index: 3, name: '○ 牛乳', amount: '大さじ2'}, {index: 4, name: '○ 溶き卵', amount: '1個分'}, {index: 5, name: '○ 塩', amount: '少々'}, {index: 6, name: '○ こしょう', amount: '少々'}, {index: 7, name: '○ サラダ油', amount: '小さじ1/2'}, {index: 8, name: '※ ブロッコリー', amount: '1/3株'}, {index: 9, name: '※ しめじ', amount: '1/2パック'}, {index: 10, name: '※ 赤パプリカ', amount: '1/2個'}, {index: 11, name: 'ケチャップ', amount: '大さじ6'}, {index: 12, name: 'とんかつソース', amount: '大さじ3'}, {index: 13, name: '水', amount: '150cc'}, ])
hamburgSteak.procedures.create([{index: 0, description: '※のブロッコリー、しめじは小房に分け、赤パプリカは2cm幅に切る。', image_url: ''}, {index: 1, description: 'ボウルにサラダ油以外の○の材料を入れてこね、4等分にし小判型にととのえる。', image_url: ''}, {index: 2, description: 'フライパンにサラダ油を熱し、(2)を入れ両面に焼き色をつける。', image_url: ''}, {index: 3, description: '(1)を加え、焼き色がついたらデミグラス風煮込みソースの材料を加える。', image_url: ''}, {index: 4, description: '沸騰したら火を弱め、フタをして煮込む。', image_url: ''}, ])

# 野菜炒め
yasaiitame = Recipe.create(title: '簡単おいしい！肉野菜炒め', comment: '簡単でおいしい、定番の肉野菜炒めのご紹介です。強火でさっと炒めたシャキシャキの野菜とジューシーな豚肉に、オイスターソースのコクが効いた味付けがよく合います。お好みのお肉や野菜を使ってアレンジもできるので、ぜひお試しくださいね。', amount: 2, calorie: 150, main_ingredient: '葉物野菜', category: '炒め物', tips: '豚こま切れ肉は、お好みの部位や形状のものに代えてもお作りいただけます。
調味料の加減は、お好みで調整してください。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/yasaiitame/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpg', user_id: 1)
yasaiitame.ingredients.create([{index: 0, name: '豚こま切れ肉', amount: '150g'}, {index: 1, name: '薄力粉', amount: '小さじ1'}, {index: 2, name: '塩こしょう', amount: 'ひとつまみ'}, {index: 3, name: 'にんじん', amount: '1/3本'}, {index: 4, name: 'キャベツ', amount: '200g'}, {index: 5, name: 'もやし', amount: '100g'}, {index: 6, name: 'ピーマン', amount: '2個'}, {index: 7, name: '(A)料理酒', amount: '大さじ1'}, {index: 8, name: '(A)しょうゆ', amount: '大さじ1/2'}, {index: 9, name: '(A)オイスターソース', amount: '大さじ1/2'}, {index: 10, name: '(A)鶏ガラスープの素', amount: '小さじ1'}, {index: 11, name: 'サラダ油', amount: '大さじ1/2'}, ])
yasaiitame.procedures.create([{index: 0, description: 'にんじんは皮をむきます。ピーマンはヘタと種を取り除いておきます。', image_url: ''}, {index: 1, description: 'にんじんは短冊切りにします。', image_url: ''}, {index: 2, description: 'キャベツは一口大に切ります。ピーマンは1cm幅に切ります。', image_url: ''}, {index: 3, description: 'ボウルに(A)を入れて混ぜ合わせます。', image_url: ''}, {index: 4, description: '豚こま切れ肉に薄力粉をまぶします。', image_url: ''}, {index: 5, description: '
中火で熱したフライパンにサラダ油をひき、4を入れて炒めます。色が変ったら塩こしょうをふり、さっと炒めて一度取り出します。', image_url: ''}, {index: 6, description: '
同じフライパンを強火で熱し、1を入れて炒めます。', image_url: ''}, {index: 7, description: 'にんじんに火が通ったら2ともやしを入れて強火で炒めます。', image_url: ''}, {index: 8, description: 'キャベツがしんなりしたら5と3を入れて強火のまま炒めます。', image_url: ''}, {index: 9, description: '全体に味がなじんだら火から下ろし、器に盛り付けて完成です。', image_url: ''}, ])

# 筑前煮
chikuzenni = Recipe.create(title: '基本の筑前煮', comment: '筑前煮は、さまざまな具材の旨み・風味が出るので、特にだし汁は必要ありません（干し椎茸の戻し汁だけ使います）。<br>はじめに具材を炒める・最後は少し煮詰めるのがポイント。日持ちもするので、ぜひたっぷり作ってみてください。', amount: 6, calorie: 109, main_ingredient: '根菜', category: '煮物', tips: '今回、筑前煮はたっぷりの量で作ったほうが美味しくなるので、少し多めのレシピになっています。日持ちも冷蔵庫で5日ほど（目安として）するものなので、ぜひ多めの分量で作ってみてください。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpeg', user_id: 1)
chikuzenni.ingredients.create([{index: 0, name: '鶏もも肉', amount: '1枚（300ｇほど）'}, {index: 1, name: '干し椎茸', amount: '4〜5枚'}, {index: 2, name: 'こんにゃく', amount: '１枚'}, {index: 3, name: 'れんこん', amount: '300ｇ'}, {index: 4, name: 'ごぼう', amount: '2/3本（100ｇほど）'}, {index: 5, name: 'にんじん', amount: '1/2本'}, {index: 6, name: '里芋', amount: '400ｇ（中サイズで5〜6個）'}, {index: 7, name: '絹さや', amount: '6〜7枚'}, {index: 8, name: 'サラダ油,米油,菜種油などの植物油', amount: '小さじ１'}, {index: 9, name: '椎茸の戻し汁', amount: '150ml使用'}, {index: 10, name: '醤油', amount: '大さじ5'}, {index: 11, name: '酒', amount: '大さじ４'}, {index: 12, name: 'みりん', amount: '大さじ４'}, ])
chikuzenni.procedures.create([{index: 0, description: 'はじめに、干し椎茸はさっと洗ってからひたひたの水に数時間浸けておきます。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E6%A4%8E%E8%8C%B8%E4%B8%8B%E3%81%93%E3%82%99%E3%81%97%E3%82%89%E3%81%88.jpeg'}, {index: 1, description: 'れんこんとごぼうは皮をむいて、食べやすい大きさの乱切りにします（ごぼうは大きめのひと口大、れんこんはそれよりも大きくてOKです）。それぞれ水にさらします。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E3%83%AC%E3%83%B3%E3%82%B3%E3%83%B3%E3%82%B3%E3%82%99%E3%83%9B%E3%82%99%E3%82%A6%E4%B8%8B%E3%81%93%E3%82%99%E3%81%97%E3%82%89%E3%81%88.jpeg'}, {index: 2, description: 'にんじんは小さめの乱切りにして、椎茸は軸を除いて2〜3等分に切ります。椎茸の戻し汁はごみがあれば茶こしなどで除き、150mlはかっておきます。', image_url: ''}, {index: 3, description: '里芋は皮をむいて大きければ2〜3等分に切ります。里芋だけは火が通りづらいので事前に下ゆでしておきます（水から火にかけ、沸いたら3〜4分ゆでてざる上げします）。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E9%87%8C%E8%8A%8B%E4%B8%8B%E3%81%93%E3%82%99%E3%81%97%E3%82%89%E3%81%88.jpeg'}, {index: 4, description: 'こんにゃくはスプーンでちぎり、塩もみしてから2〜3分ゆでてざる上げします。', image_url: ''}, {index: 5, description: '鶏肉は余分な脂や皮があれば切り落とし、皮を下にして3〜4㎝四方に切ります。これで具材の下ごしらえの完了です。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E5%85%A8%E3%81%A6%E3%81%AE%E6%9D%90%E6%96%99.jpeg'}, {index: 6, description: '大きめの鍋やフライパンに油小さじ1を入れて弱めの中火にかけます。', image_url: ''}, {index: 7, description: '鶏肉、水気を切ったれんこん、ごぼう、にんじん、こんにゃくを加えて炒め合わせます。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E7%82%92%E3%82%81%E3%82%8B.jpeg'}, {index: 8, description: '鶏肉や根菜類を炒め合わせることで、油に香りが移ってより美味しくなります。全体に油がまわって、野菜の表面に少し透明感が出てくるまで炒めましょう。', image_url: ''}, {index: 9, description: '里芋と干し椎茸を加えます。続けて椎茸の戻し汁150mlと調味料（醤油大さじ5、酒とみりん各大さじ4）を加えてから混ぜ合わせます。', image_url: ''}, {index: 10, description: '落し蓋をして煮ていきます。煮るときの火加減は弱めの中火、煮る時間は【10分】です。', image_url: ''}, {index: 11, description: '鍋肌ではぐつぐつと煮汁が沸くのが見え、落し蓋の下の煮汁もしっかり煮汁が対流する沸き具合を保つことが大切です。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E7%85%AE%E3%82%8B.jpeg'}, {index: 12, description: '完全に煮汁を飛ばすと味が濃くなるので、時おり具材全体を混ぜながら煮詰め、煮汁が少なくなって鍋底一面に煮汁が残るくらいになれば一度味見をして確認してみましょう。', image_url: ''}, {index: 13, description: '煮詰め加減が味の濃さにもつながるので、好みの加減になれば火を止め、食べる時に塩ゆでした絹さやを散らすなどすると色合いもきれいに仕上がります。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/chikuzenni/%E5%AE%8C%E6%88%90%E9%96%93%E8%BF%91.jpeg'}, ])

# かぼちゃの煮物
kabocha = Recipe.create(title: 'レンジで簡単☆かぼちゃの煮物', comment: 'レンジで出来るので簡単です。水分を使わないのでホクホクかぼちゃの煮物です。', amount: 3, calorie: 128, main_ingredient: '果菜', category: '煮物', tips: 'コンロを使わず火加減も気にしなくていいので簡単です。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/kabocha/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpeg', user_id: 1)
kabocha.ingredients.create([{index: 0, name: 'かぼちゃ', amount: 'かぼちゃ1/4個(400gくらい)'}, {index: 1, name: '砂糖', amount: '大さじ1'}, {index: 2, name: 'しょうゆ', amount: '大さじ1/2'}, ])
