kakiNabe.procedures.create([{index: 0, description: '生牡蠣は下処理すべきものは塩水に入れ洗い流し、水気を切ります。<br>白菜は食べやすく切り、えのきは石づきを切り取りほぐします。<br>豆腐は食べやすく切ります。', image_url: ''}, {index: 1, description: '鍋に○を入れ火にかけ、白菜、えのき、豆腐を入れます。<br>白菜がしんなりしたら、牡蠣を入れ一煮立ちして牡蠣がぷっくりし、火が入ると、出来上がり。', image_url: ''}, ])

# 豆腐入りフワフワお好み焼き
tofuOkonomiyaki = Recipe.create(title: '豆腐入りフワフワお好み焼き', comment: '絹ごし豆腐入りのフワフワなお好み焼きのご紹介です。とろけるチーズのコクとあげ玉のさくさくの食感がおいしくてついついお箸が止まりませんよ。ビールにもよく合いますのでちょっとしたおもてなしにも喜ばれます。お手軽な材料でできますので今晩のおかずにぜひ、お試しくださいね。', amount: 2, calorie: 578, main_ingredient: '豆腐', category: '粉物', tips: '絹ごし豆腐は木綿豆腐でも代用できます。<br>トッピングの材料はお好みで調整してください。<br>ピザ用チーズはお好みのチーズで代用できます。<br>最後に裏返して強火で焼くことでパリッとおいしく仕上がります。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/tofuOkonomiyaki/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpg', user_id: 1)
tofuOkonomiyaki.ingredients.create([{index: 0, name: '<具材>キャベツ', amount: '200g'}, {index: 1, name: '<具材>ピザ用チーズ', amount: '50g'}, {index: 2, name: '<具材>あげ玉', amount: '20g'}, {index: 3, name: '<具材>釜揚げしらす', amount: '25g'}, {index: 4, name: '<生地>絹ごし豆腐', amount: '150g'}, {index: 5, name: '<生地>卵', amount: '2個'}, {index: 6, name: '<生地>薄力粉', amount: '大さじ4'}, {index: 7, name: '<生地>マヨネーズ', amount: '大さじ1'}, {index: 8, name: '<生地>ベーキングパウダー', amount: '小さじ1/2'}, {index: 9, name: 'ごま油', amount: '大さじ2'}, {index: 10, name: '<トッピング>お好み焼きソース', amount: '大さじ2'}, {index: 11, name: '<トッピング>マヨネーズ', amount: '大さじ2'}, {index: 12, name: '<トッピング>かつお節', amount: '2g'}, {index: 13, name: '<トッピング>青のり', amount: '適量'}, ])
tofuOkonomiyaki.procedures.create([{index: 0, description: 'キャベツは粗みじん切りにします。', image_url: ''}, {index: 1, description: 'ボウルに生地の材料を入れてホイッパーで混ぜ合わせ、1、残りの具材の材料を入れてよく混ぜ合わせます。', image_url: ''}, {index: 2, description: 'フライパンに半量のごま油を入れて中火にし、2の半量を入れて焼きます。', image_url: ''}, {index: 3, description: '焼き色がついたら裏返し蓋をして5分程焼きます。', image_url: ''}, {index: 4, description: '焼き色がつき、中まで火が通ったら表に返して強火にし、1分程焼いたら火から下ろします。同様にもう1枚焼きます。', image_url: ''}, {index: 5, description: '食べやすい大きさに切り、器に盛り付けてお好み焼きソース、マヨネーズ、かつお節、青のりをかけて出来上がりです。', image_url: ''}, ])

# 本格餃子
gyoza = Recipe.create(title: '本格餃子', comment: 'シャキシャキ野菜をたっぷり混ぜ混みしっかりと下味を付けました！', amount: 2, calorie: 480, main_ingredient: 'ひき肉', category: '餃子・焼売・春巻き', tips: '10の盛り付け方は水分や油が残っていると火傷の原因になり危ないので注意して下さい。<br>不慣れな場合は無理をせずフライ返し等で取って盛り付けて下さい。<br>26㎝のフライパン(必ずくっつかないフライパンを使う)にぎっしり並べると一度で焼けます。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyoza/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpeg', user_id: 1)
gyoza.ingredients.create([{index: 0, name: '餃子の皮(普通のサイズ)', amount: '25～30枚位'}, {index: 1, name: '豚ひき肉', amount: '200g'}, {index: 2, name: 'キャベツ', amount: '200g'}, {index: 3, name: '青ネギ(白ネギでも可)', amount: '50g(1/2本)'}, {index: 4, name: 'にら', amount: '50g(1/2本)'}, {index: 5, name: '☆鶏ガラスープの素(顆粒)', amount: '2g(小1/2)'}, {index: 6, name: '☆酒', amount: '5g(小1)'}, {index: 7, name: '☆みりん', amount: '6g(小1)'}, {index: 8, name: '☆濃口醤油', amount: '6g(小1)'}, {index: 9, name: '☆おろしにんにく', amount: '5g(小1)'}, {index: 10, name: '☆おろししょうが', amount: '3g(小1弱)'}, {index: 11, name: '☆ごま油', amount: '4g(小1)'}, {index: 12, name: '塩(野菜の塩揉み用)', amount: '5g'}, {index: 13, name: '塩', amount: '少々'}, {index: 14, name: 'こしょう', amount: '少々'}, {index: 15, name: 'ごま油(焼く時)', amount: '15g(大1強)'}, {index: 16, name: '<タレ>濃口醤油', amount: '大1(18g)'}, {index: 17, name: '<タレ>薄口醤油', amount: '大1(18g)'}, {index: 18, name: '<タレ>砂糖', amount: '大1(9g)'}, {index: 19, name: '<タレ>酢', amount: '大2/3(10g)'}, {index: 20, name: '<タレ>ラー油(お好みで)', amount: '適量'}, ])
gyoza.procedures.create([{index: 0, description: 'キャベツ、ニラ、青ネギをそれぞれ小さく切る。塩をかけて全体に混ぜ野菜がしんなりするまでしばらくおく。', image_url: ''}, {index: 1, description: '☆を混ぜ合わせておく。(餃子の下味用)', image_url: ''}, {index: 2, description: 'タレの材料は混ぜ合わせておく。', image_url: ''}, {index: 3, description: 'ひき肉に塩こしょう、2の調味料を加えてしっかり混ぜる。', image_url: ''}, {index: 4, description: '1の野菜がしんなりしたら手で水分を絞ってひき肉と混ぜ合わせる。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyoza/%E6%89%8B%E9%A0%865.jpeg'}, {index: 5, description: '餃子の皮に具を乗せ、縁に水を付け、ひだをつけながら包む。
(具が多すぎると包みにくくなるのでで加減して下さい)', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyoza/%E6%89%8B%E9%A0%866.jpeg'}, {index: 6, description: '包んだ餃子はキッチンペーパーを敷いたお皿の上に並べる。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyoza/%E6%89%8B%E9%A0%867.jpeg'}, {index: 7, description: 'ごま油を全体に塗ったフライパン(26㎝)に餃子を綺麗に並べて火を付ける。強火で軽く焼き色が付くまで焼く。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/gyoza/%E6%89%8B%E9%A0%868.jpeg'}, {index: 8, description: '水100ccを全体に回しかけ、すぐに蓋をし中火で5分焼く。
(片手に水、片手に蓋を持ってすぐに蓋をする！！)', image_url: ''}, {index: 9, description: '蓋を外して水分が完全に無くなり、焼き色をつけた面がカラッとするまで焼く。
水分が無くなると焦げやすくなるので注意する！', image_url: ''}, {index: 10, description: 'フライパンを揺すって餃子がくっついていない事を確認し、サイズの合うお皿を乗せてしっかり押さえながら裏返して盛り付ける。', image_url: ''}, ])

# 豚の生姜焼き
shogayaki = Recipe.create(title: 'ご飯が進む！豚肉の生姜焼き', comment: '今晩のおかずに、ごはんとの相性抜群の豚の生姜焼きはいかがでしょうか。さわやかな生姜の香りのたれがしっかり絡んだ肉は、ごはんのおかずだけでなく、お酒のおつまみにもピッタリです。キャベツをたっぷり添えてお召し上がりくださいね。', amount: 2, calorie: 374, main_ingredient: '豚肉', category: '肉料理', tips: 'ごま油は、サラダ油でも代用いただけます。<br>豚ロースは、お好みの部位に代えてもお作りいただけます。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/shogayaki/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpg', user_id: 1)
shogayaki.ingredients.create([{index: 0, name: '豚ロース(薄切り)', amount: '300g'}, {index: 1, name: '薄力粉', amount: '大さじ1'}, {index: 2, name: '<たれ>すりおろし生姜', amount: '<たれ>大さじ2'}, {index: 3, name: '<たれ>しょうゆ', amount: '大さじ2'}, {index: 4, name: '<たれ>砂糖', amount: '大さじ1'}, {index: 5, name: '料理酒', amount: '大さじ1'}, {index: 6, name: 'ごま油', amount: '大さじ1'}, {index: 7, name: '<付け合わせ>キャベツ', amount: '150g'}, {index: 8, name: '<付け合わせ>トマト', amount: '1個'}, ])
shogayaki.procedures.create([{index: 0, description: 'キャベツを千切りにして、トマトは一口大に切ります。', image_url: ''}, {index: 1, description: '豚ロースに薄力粉をまぶします。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/shogayaki/%E7%89%87%E6%A0%97%E7%B2%89%E3%82%92%E3%81%BE%E3%81%B5%E3%82%99%E3%81%99.jpeg'}, {index: 2, description: 'ボウルにたれの材料を入れて混ぜ合わせます。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/shogayaki/%E8%AA%BF%E5%91%B3%E6%96%99%E3%82%92%E6%B7%B7%E3%81%9B%E3%82%99%E5%90%88%E3%82%8F%E3%81%9B%E3%82%8B.jpeg'}, {index: 3, description: '中火で熱したフライパンにごま油をひき、2を入れて火が通り、両面にこんがりと焼き色が付くまで焼き、3を入れます。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/shogayaki/%E8%82%89%E3%82%92%E7%84%BC%E3%81%84%E3%81%A6%E8%AA%BF%E5%91%B3%E6%96%99%E3%82%92%E5%85%A5%E3%82%8C%E3%82%8B.jpeg'}, {index: 4, description: '中火で炒め合わせ、全体に味がなじんだら火から下ろします。', image_url: ''}, {index: 5, description: '1をのせたお皿に5を盛り付けて完成です。', image_url: ''}, ])

# ブロッコリーとツナのサラダ
tunaSalad = Recipe.create(title: 'ブロッコリーとツナのサラダ', comment: 'ツナとコーンとマヨネーズで、野菜嫌いな子どももモリモリ美味しく食べられます♪', amount: 2, calorie: 148, main_ingredient: 'ツナ缶', category: 'サラダ', tips: 'コーンは冷凍でなくても、何でもOKです。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/tunasalad/%E3%83%A1%E3%82%A4%E3%83%B3%E7%94%BB%E5%83%8F.jpeg', user_id: 1)
tunaSalad.ingredients.create([{index: 0, name: 'ブロッコリー', amount: '1株'}, {index: 1, name: '人参', amount: '1/4本'}, {index: 2, name: '冷凍コーン', amount: '好きなだけ'}, {index: 3, name: 'ごま', amount: '大匙2'}, {index: 4, name: 'ツナ', amount: '1缶'}, {index: 5, name: 'マヨネーズ', amount: '大さじ5~6'}, {index: 6, name: '麺つゆ', amount: '小さじ2'}, ])
tunaSalad.procedures.create([{index: 0, description: 'ブロッコリーは食べやすい大きさに、人参は千切りにし、一緒に2分くらい（好みの固さに合わせてお好みで）塩茹で。', image_url: 'https://smart-kitchen-dev.s3.ap-northeast-1.amazonaws.com/tunasalad/%E8%8C%B9%E3%81%A6%E3%82%99%E3%82%8B.jpeg'}, {index: 1, description: '冷凍コーンの場合はここで、茹でたブロッコリーの上にパラパラ乗せておきます。', image_url: ''} ])

